const burgerBtn=document.querySelector(".header__burger"),bodyEl=document.querySelector("body");burgerBtn.addEventListener("click",(()=>{bodyEl.classList.toggle("burger-active")}));const allInputs=document.querySelectorAll("form input"),textAreas=document.querySelectorAll("form textarea");if(allInputs.length>0)for(let e=0;e<allInputs.length;e++){let t=allInputs[e],l=t.placeholder;t.addEventListener("click",(function(){t.placeholder=""})),document.addEventListener("click",(function(e){e.target!==t&&(t.placeholder=l)}))}const submenuBtn=document.querySelector(".toggle-submenu");submenuBtn.addEventListener("click",(()=>{submenuBtn.closest(".nav-header__item").classList.toggle("submenu-active")}));const titlesFooter=document.querySelectorAll(".menu-footer__title");for(i=0;i<titlesFooter.length;i++){let e=titlesFooter[i];e.addEventListener("click",(()=>{e.closest(".menu-footer__nav").classList.toggle("footer-menu-open")}))}